	/**
	 * 
	 */
	public void initDropItemProp() {
		String[] dropCount = this.dropItemProp.split(",");
		int i = 0;
		for (String s : dropCount) {
			String[] one = s.split(":");
			if (one.length == 2) {
				for (int k = 0; k < Integer.valueOf(one[1]).intValue() && i < 100; ++k, ++i) {
					dropItemCountProp[i] = Integer.valueOf(one[0]).byteValue();
				}
			}
		}
		String[] dropLevel = this.dropItemLevel.split(",");
		i = 0;
		for (String s : dropLevel) {
			String[] one = s.split(":");
			if (one.length == 2) {
				for (int k = 0; k < Integer.valueOf(one[1]).intValue() && i < 100; ++k, ++i) {
					dropItemLevelProp[i] = Integer.valueOf(one[0]).byteValue();
				}
			}
		}
	}
	/**
	 * 
	 */
	public Monster() {
		player = null;
		monster = null;
	}
	/**
	 * 
	 * @param cm
	 * @param cmm
	 * @param m
	 * @param identify
	 */
	public void initInstance(CityFacilityMonster cm, Monster cmm, Monster m, Integer identify) {
		if(cmm.getIsInstance().intValue()==0){
			m = cmm;
		}
		this.monster = cmm;
		this.setInstanceMonster(m);
		this.cityFacilityMonster = cm;
		player = new Player();
//		player.init();
		player.setIfIsMonster(true);
		player.setId(identify);
		player.setName(cmm.getName());
		player.setSettingFlag(0);
		this.setShowName(cmm.getName());
		player.setBelongMonster(cmm);
		player.setSectId(0);
		player.setHp(m.getMaxHp());
		///player.setMaxHp(m.getMaxHp());
		player.setLevel(m.getLevel());
		
		PlayerDyn prop = new PlayerDyn();
		prop.changeMaxHp(m.getMaxHp());
		prop.changeAttackMax(m.getAttackMax());
		prop.changeAttackMin(m.getAttackMin());
		prop.changeDefence(m.getDefence());
		
		player.setDyn(prop);
	}
	
	/**
	 * 
	 * @param m
	 * @param identify
	 */
	public void init(CityFacilityMonster cm, Monster m, Integer identify) {
		this.monster = m;
		this.cityFacilityMonster = cm;
		player = new Player();
		//player.init();
		player.setIfIsMonster(true);
		player.setId(identify);
		player.setName(m.getName());
		player.setSettingFlag(0);
		this.setShowName(m.getName());
		player.setHp(m.getMaxHp());
		///player.setMaxHp(m.getMaxHp());
		player.setBelongMonster(m);
		player.setLevel(m.getLevel());
		player.setSectId(0);
		PlayerDyn prop = new PlayerDyn();
		prop.changeMaxHp(m.getMaxHp());
		
		prop.changeAttackMax(m.getAttackMax());
		prop.changeAttackMin(m.getAttackMin());
		prop.changeDefence(m.getDefence());
		
		player.setDyn(prop);
		monsterType = m.getMonsterType();
		updateAutoRestoreInterval();
	}
