	public int updateAutoRestoreInterval() {
		try {
			int pos = random.nextInt(100);
			int[] pool = MonsterMG.instance.getAutoRestoreHpConfPool(this.getMonster().getMonsterType());
			autoRestoreInterval = pool[pos];
		} catch (Exception e) {
			return 0;
		}
		return autoRestoreInterval;
	}
	
	public int autoRestoreHp() {
		long now = System.currentTimeMillis();
		if (autoRestoreInterval > 0 && (this.getPlayer().getHp() < this.monster.getMaxHp()) && (now - lastAutoRestorePoint > autoRestoreInterval * 1000)) {
			lastAutoRestorePoint = now;
			int tmp = this.getPlayer().getDyn().getMaxHp() - player.getHp();
			player.setHp(this.getPlayer().getDyn().getMaxHp());
			//player.getDyn().setMaxHp(this.monster.getMaxHp());
			updateAutoRestoreInterval();
			//System.out.println("autoRestore success!!!!!autoRestoreInterval="+autoRestoreInterval);
			return tmp;
		}
		return 0;
	}
	
